    private void AvailBloodDonorTableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_AvailBloodDonorTableMouseClicked
        int row = AvailBloodDonorTable.getSelectedRow();
        int value = Integer.parseInt(AvailBloodDonorTable.getModel().getValueAt(row, 0).toString());

        con = DBSCon.dataBaseConnection();
        try
        {
            Statement st = con.createStatement();
            String queery = "select * from Request where id = '"+value+"'";
            ResultSet rs = st.executeQuery(queery);

            if (rs.next())
            {

                IdRequesttxt.setText(rs.getString(1));
                NameRequesttxt.setText(rs.getString(2));
                BankRequesttxt.setText(rs.getString(3));
                CityRequestCombo.setSelectedItem(rs.getString(4));
                BTypeRequestCombo.setSelectedItem(rs.getString(5));
                LocationRequesttxt.setText(rs.getString(6));
                BottleRequestCombo.setSelectedItem(rs.getString(7));
                Date date = rs.getDate(8);
                RequestDateChooser.setDate(date);

            }
        }catch(Exception ex)
        {

        }
        finally
        {
            try
            {
                con.close();
            }catch(Exception ex)
            {

            }
        }

    }//GEN-LAST:event_AvailBloodDonorTableMouseClicked

    private void RUpdateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RUpdateButtonActionPerformed
        con = DBSCon.dataBaseConnection();
        String date = ((JTextField)RequestDateChooser.getDateEditor().getUiComponent()).getText();
        try
        {
            Statement st = con.createStatement();
            String queery = "update Request set Name = '"+NameRequesttxt.getText()+"', BloodBank = '"+BankRequesttxt.getText()+"', BloodType = '"+BTypeRequestCombo.getSelectedItem()+"',Location = '"+LocationRequesttxt.getText()+"', Bottles = '"+BottleRequestCombo.getSelectedItem()+"',Date = '"+date+"' where Id = '"+Integer.parseInt(IdRequesttxt.getText())+"' and City = '"+CityRequestCombo.getSelectedItem()+"' ";
            st.executeUpdate(queery);
            JOptionPane.showMessageDialog(null, "Data Updated ");
            showSpecificBloodData(Integer.parseInt(MainForm.getID()));
        }catch(Exception ex)
        {

        }
        finally
        {
            try
            {
                con.close();
            }catch(Exception ex)
            {

            }
        }
    }//GEN-LAST:event_RUpdateButtonActionPerformed

