    private void IdRequesttxtKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_IdRequesttxtKeyPressed
        String IdRequest = IdRequesttxt.getText();
        int length = IdRequest.length();
        char c = evt.getKeyChar();
        
        if (evt.getKeyChar()>='0' && evt.getKeyChar()<='9')
        {
            if(length<5){
                IdRequesttxt.setEditable(true);
            }else
            {
                IdRequesttxt.setEditable(false);
            }
        }else
        {
            if(evt.getExtendedKeyCode()==KeyEvent.VK_BACKSPACE || evt.getExtendedKeyCode()==KeyEvent.VK_DELETE)
            {
                IdRequesttxt.setEditable(true);
            }else
            {
                IdRequesttxt.setEditable(false);
            }
        }
    }//GEN-LAST:event_IdRequesttxtKeyPressed

    private void NameRequesttxtKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_NameRequesttxtKeyPressed
        char c = evt.getKeyChar();
        if (Character.isLetter(c)||Character.isWhitespace(c)||Character.isISOControl(c)){
            NameRequesttxt.setEditable(true);
            
        }else{
            NameRequesttxt.setEditable(false);
        }
    }//GEN-LAST:event_NameRequesttxtKeyPressed

